class Solution:
    def rangeSum(self, nums: list[int], n: int, left: int, right: int) -> int:
        MOD = 10**9 + 7
        min_heap = []

        # Generar todas las sumas de subarreglos y mantenerlas en un heap
        for i in range(n):
            current_sum = 0
            for j in range(i, n):
                current_sum += nums[j]
                heapq.heappush(min_heap, current_sum)

        # Extraer los elementos del heap hasta alcanzar el rango deseado
        result = 0
        for _ in range(right):
            smallest_sum = heapq.heappop(min_heap)
            if _ >= left - 1:
                result = (result + smallest_sum) % MOD

        return result
